function ToggleFields()
{
	var objCheckBox = document.getElementById("CheckBoxCreateListItem");
	var objTable = document.getElementById("FieldTable").childNodes(0).childNodes;
	if (objCheckBox == null)
		return;
	if (objTable == null)
		return;
	if (objCheckBox.checked == false)
	{
		for(var i=0;i<objTable.length;i++)
		{
			var childOfTD = objTable.item(i).childNodes(1).children; 
			for(var j=0;j<childOfTD.length;j++)
			{
				if (childOfTD.item(j).tagName == "TABLE")
				{
					DisableChildren(childOfTD.item(j));
				}
				else
				{
					childOfTD.item(j).disabled = true;
				}
			}

		}
	}
	else
	{
		for(var i=0;i<objTable.length;i++)
		{
			var childOfTD = objTable.item(i).childNodes(1).children; 
			for(var j=0;j<childOfTD.length;j++)
			{

				if (childOfTD.item(j).tagName == "TABLE")
				{
					EnableChildren(childOfTD.item(j));
				}
				else
				{
					childOfTD.item(j).disabled = false;
				}
			}
		}

	}
}
function DisableChildren(objChild)
{
	var objChildNodes = objChild.childNodes(0).childNodes;
	if(objChildNodes.length > 0)
	{
		for(var i=0;i<objChildNodes.length;i++)
		{
			var objChildOfTD = objChildNodes.item(i).childNodes(0).children;
			for (var j=0;j<objChildOfTD.length;j++)
			{
				objChildOfTD.item(j).disabled = true;
			}
		}
	}
	return;
}
function EnableChildren(objChild)
{
	var objChildNodes = objChild.childNodes(0).childNodes;
	if(objChildNodes.length > 0)
	{
		for(var i=0;i<objChildNodes.length;i++)
		{
			var objChildOfTD = objChildNodes.item(i).childNodes(0).children;
			for (var j=0;j<objChildOfTD.length;j++)
			{
				objChildOfTD.item(j).disabled = false;
			}
		}
	}
}

function PreFillForm()
{
	var sState = "complete";
	var re = /\+/g;
	var thisForm = document.forms[

];
	if ((!thisForm.readyState) || ((thisForm.readyState) && (sState == thisForm.readyState)))
	{

		var objCheckBox = document.getElementById(chkID);
		if (objCheckBox == null)
			return;

		var url = document.location.href;
		var siteurl = GetUrlKeyValue("SiteUrl",url);
		if (siteurl == "")
			return;
		siteurl = unicodeFromUtf8(unescape(siteurl));
		siteurl = siteurl.replace(re," ");
		var sitetitle = GetUrlKeyValue("Title",url);
		sitetitle = unicodeFromUtf8(unescape(sitetitle));
		sitetitle = sitetitle.replace(re," ");
		var sitedesc = GetUrlKeyValue("Description",url);
		sitedesc = unicodeFromUtf8(unescape(sitedesc));
		sitedesc = sitedesc.replace(re," ");

		document.getElementsByName("urn:schemas-microsoft-com:office:office#Title").item(0).value = sitetitle;
		document.getElementById("HiddenTitle").value = sitetitle;	
		document.getElementsByName("OWS:URL:URL").item(0).value = siteurl;
		document.getElementsByName("OWS:URL:Desc").item(0).value = siteurl;
		document.getElementById("HiddenURL").value = siteurl;

		document.getElementsByName("urn:schemas-microsoft-com:office:office#Description").item(0).value= sitedesc;
		document.getElementById("HiddenDesc").value = sitedesc;
		document.getElementById("NextUsing").value = siteurl;
	}
}

function unicodeFromUtf8(strUtf8) 
{
	var bstr = "";
	var nTotalChars = strUtf8.length;	
	var nOffset = 0;					
	var nRemainingBytes = nTotalChars;	
	var nOutputPosition = 0;
	var iCode, iCode1, iCode2;			

	while (nOffset < nTotalChars)
	{
		iCode = strUtf8.charCodeAt(nOffset);
		if ((iCode & 0x80) == 0)			
		{
			if ( nRemainingBytes < 1 )		
				break;

			bstr += String.fromCharCode(iCode & 0x7F);
			nOffset ++;
			nRemainingBytes -= 1;
		}
		else if ((iCode & 0xE0) == 0xC0)	
		{
			iCode1 =  strUtf8.charCodeAt(nOffset + 1);
			if ( nRemainingBytes < 2 ||			
				 (iCode1 & 0xC0) != 0x80 )		
			{
				break;
			}

			bstr += String.fromCharCode(((iCode & 0x3F) << 6) | (	 iCode1 & 0x3F));
			nOffset += 2;
			nRemainingBytes -= 2;
		}
		else if ((iCode & 0xF0) == 0xE0)	
		{
			iCode1 =  strUtf8.charCodeAt(nOffset + 1);
			iCode2 =  strUtf8.charCodeAt(nOffset + 2);
			if ( nRemainingBytes < 3 ||			
				 (iCode1 & 0xC0) != 0x80 ||		
				 (iCode2 & 0xC0) != 0x80 )
			{
				break;
			}

			bstr += String.fromCharCode(((iCode & 0x0F) << 12) | 
					((iCode1 & 0x3F) <<  6) |
					(iCode2 & 0x3F));
			nOffset += 3;
			nRemainingBytes -= 3;
		}
		else								
			break;
	}

	if (nRemainingBytes != 0)
	{

		return "";
	}

	return bstr;
}
if(browseris.nav)
{
	window.captureEvents(Event.LOAD);
	window.onload = PreFillForm;	
}
else
{
	document.onreadystatechange = PreFillForm;
}

